{function postLikeBtn(e){$(e).click((function(t){t.preventDefault(),$.ajax({method:"Post",url:$(e).prop("href"),success:function(t){t.data&&t.data.deleted?($("i",e).addClass("w-like"),$("i",e).removeClass("like")):($("i",e).addClass("like"),$("i",e).removeClass("w-like")),$("span",e).html(t.data.likeCount)},error:function(e){console.log("error in postLikeBtn",e),errorMsg(e.responseText)}})}))}function cmtLikeBtn(e){$(e).click((function(t){t.preventDefault(),$.ajax({method:"Post",url:$(e).prop("href"),success:function(t){t.data&&t.data.deleted?($("i",e).addClass("g-like"),$("i",e).removeClass("like")):($("i",e).addClass("like"),$("i",e).removeClass("g-like")),$("span",e).html(t.data.likeCount)},error:function(e){console.log("error in cmtLikeBtn",e),errorMsg(e.responseText)}})}))}function likeBtnHandler(){let e=$(".postLikeBtn"),t=$(".cmtLikeBtn");for(let t of e)postLikeBtn(t);for(let e of t)cmtLikeBtn(e)}likeBtnHandler()}